import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './CustomCalendar.css';

const CustomCalendar = () => {
  const [value, setValue] = useState(new Date());

  const handleTodayButtonClick = () => {
    setValue(new Date());
  };

  return (
    <div className="custom-calendar-container">
      <div className="calendar-header">
        <h1>React Calendar</h1>
        <button className="today-button" onClick={handleTodayButtonClick}>Today</button>
      </div>
      <Calendar
        onChange={setValue}
        value={value}
        tileContent={({ date, view }) =>
          view === 'month' && date.getDate() === new Date().getDate() && date.getMonth() === new Date().getMonth() && date.getFullYear() === new Date().getFullYear() ? (
            <div className="highlight-today">❤️</div>
          ) : null
        }
      />
    </div>
  );
};

export default CustomCalendar;
